import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const CheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);
    setSuccess(null);

    const cardElement = elements.getElement(CardElement);

    // Llamada a tu backend para crear el PaymentIntent
    const response = await fetch('http://localhost:5000/create-payment-intent', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ amount: 1000 }), // Monto en centavos
    });

    const { clientSecret } = await response.json();

    const { error } = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: cardElement,
      },
    });

    if (error) {
      setError(error.message);
    } else {
      setSuccess('Pago exitoso');
    }
  };

  // Estilos actualizados para hacer más grandes los campos
  const cardElementOptions = {
    style: {
      base: {
        fontSize: '24px', // Aumenta el tamaño del texto a 24px
        color: '#424770',
        letterSpacing: '0.05em',
        fontFamily: 'Source Code Pro, monospace',
        '::placeholder': {
          color: '#aab7c4',
        },
      },
      invalid: {
        color: '#9e2146',
      },
    },
  };

  return (
    <form onSubmit={handleSubmit} style={{ fontSize: '24px' }}>
      <CardElement options={cardElementOptions} />
      <button
        type="submit"
        disabled={!stripe}
        style={{
          fontSize: '20px', // Aumenta el tamaño del botón
          padding: '15px 30px', // Aumenta el padding del botón
          marginTop: '20px', // Añade margen superior para separar el botón
        }}
      >
        Pagar
      </button>
      {error && <div style={{ fontSize: '18px', color: 'red' }}>{error}</div>}
      {success && <div style={{ fontSize: '18px', color: 'green' }}>{success}</div>}
    </form>
  );
};

export default CheckoutForm;
