import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const CheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [amount, setAmount] = useState(0);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);
    setSuccess(null);

    const cardElement = elements.getElement(CardElement);

    try {
      // Llamada a tu backend para crear el PaymentIntent
      const response = await fetch('http://localhost:5001/create-payment-intent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: amount * 100 }), // Convertir a centavos
      });

      const { clientSecret } = await response.json();
      console.log('Client Secret:', clientSecret);

      if (!clientSecret) {
        throw new Error('No se recibió el clientSecret');
      }

      const { error } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        },
      });

      if (error) {
        setError(error.message);
      } else {
        setSuccess('Pago exitoso');
      }

    } catch (err) {
      setError(`Error en la transacción: ${err.message}`);
    }
  };

  // Estilos personalizados para el CardElement
  const cardElementOptions = {
    style: {
      base: {
        fontSize: '24px', // Cambia este tamaño
        color: '#32325d',
        '::placeholder': {
          color: '#aab7c4',
        },
      },
      invalid: {
        color: '#fa755a',
        iconColor: '#fa755a',
      },
    },
  };

  return (
    <form onSubmit={handleSubmit}>
      <label style={{ fontSize: '24px' }}>Monto a pagar:</label>
      <input
        type="number"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
        style={{ fontSize: '24px', marginBottom: '20px' }}
      />
      <CardElement options={cardElementOptions} />
      <button
        type="submit"
        disabled={!stripe}
        style={{ fontSize: '24px', backgroundColor: 'green', color: 'white', padding: '10px 20px', marginTop: '20px' }}
      >
        Pagar
      </button>
      {error && <div style={{ color: 'red', fontSize: '18px' }}>{error}</div>}
      {success && <div style={{ color: 'green', fontSize: '18px' }}>{success}</div>}
    </form>
  );
};

export default CheckoutForm;
