import React, { useState } from 'react';
import { CardElement, useStripe, useElements } from '@stripe/react-stripe-js';

const CheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [amount, setAmount] = useState(''); // Estado para el monto

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError(null);
    setSuccess(null);

    const cardElement = elements.getElement(CardElement);

    try {
      // Llamada a tu backend para crear el PaymentIntent
      const response = await fetch('http://localhost:5001/create-payment-intent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ amount: parseInt(amount) * 100 }), // Monto en centavos
      });

      const { clientSecret } = await response.json();
      console.log('Client Secret:', clientSecret);

      if (!clientSecret) {
        throw new Error('No se recibi贸 el clientSecret');
      }

      const { error } = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: cardElement,
        },
      });

      if (error) {
        setError(error.message);
      } else {
        setSuccess('Pago exitoso');
      }

    } catch (err) {
      setError(`Error en la transacci贸n: ${err.message}`);
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ fontSize: '20px' }}>
      <label>
        Monto a pagar:
        <input
          type="number"
          value={amount}
          onChange={(e) => setAmount(e.target.value)} // Actualiza el estado con el monto
          required
          style={{ fontSize: '20px', padding: '10px', margin: '10px 0' }}
        />
      </label>
      <CardElement style={{ fontSize: '20px', padding: '10px', margin: '10px 0' }} />
      <button
        type="submit"
        disabled={!stripe}
        style={{
          fontSize: '20px',
          padding: '15px 30px',
          backgroundColor: '#4CAF50',
          color: 'white',
          border: 'none',
          cursor: 'pointer',
        }}
      >
        Pagar
      </button>
      {error && <div style={{ color: 'red', marginTop: '10px' }}>{error}</div>}
      {success && <div style={{ color: 'green', marginTop: '10px' }}>{success}</div>}
    </form>
  );
};

export default CheckoutForm;
